mapper.select:
SELECT a.* FROM attendance a
AND a.user_id IN (SELECT id FROM users WHERE username LIKE CONCAT('%', #{username}, '%')) AND a.group_id IN (SELECT id FROM groups WHERE group_name LIKE CONCAT('%', #{groupName}, '%')) AND a.check_in BETWEEN #{begin} AND #{end}
ORDER BY a.check_in DESC

select * from attendance

    user_id = #{userIdNow}

        and check_in between #{begin} and #{end}


order by check_in desc

